import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import SuccessModal from '../components/SuccessModal';
import './Wallet.css';

const Wallet = () => {
  const { user } = useAuth();
  const [creditBalance, setCreditBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [depositAmount, setDepositAmount] = useState('');
  const [depositReceipt, setDepositReceipt] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [depositError, setDepositError] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [successData, setSuccessData] = useState(null);

  useEffect(() => {
    fetchCreditWallet();
    fetchTransactions();
  }, []);

  const fetchCreditWallet = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/wallet/credit', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.status === 'success') {
        setCreditBalance(res.data.balance || 0);
      }
    } catch (error) {
      console.error('Failed to fetch credit wallet:', error);
      setError('Failed to fetch wallet data');
    }
  };

  const fetchTransactions = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/transactions', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTransactions(res.data.data || []);
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch transactions:', error);
      setLoading(false);
    }
  };

  const handleDepositSubmit = async () => {
    if (!depositAmount || !depositReceipt) {
      setDepositError('Please enter amount and upload payment proof');
      return;
    }

    const amount = parseFloat(depositAmount);
    if (isNaN(amount) || amount <= 0) {
      setDepositError('Please enter a valid amount');
      return;
    }

    if (amount < 100) {
      setDepositError('Minimum deposit amount is â‚±100');
      return;
    }

    setIsSubmitting(true);
    setDepositError('');

    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('amount', amount);
      formData.append('receipt', depositReceipt);
      formData.append('type', 'deposit');
      formData.append('description', `Deposit request of â‚±${amount.toLocaleString()}`);

      const res = await axios.post('/api/v1/payments/deposit', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      if (res.data.status === 'success') {
        setSuccessData({
          amount: amount,
          message: 'Deposit request submitted successfully! Your deposit will be processed within 24 hours.'
        });
        setShowSuccessModal(true);
        setDepositAmount('');
        setDepositReceipt(null);
        // Reset file input
        const fileInput = document.getElementById('receipt-upload');
        if (fileInput) fileInput.value = '';
      } else {
        setDepositError(res.data.message || 'Deposit submission failed');
      }
    } catch (err) {
      console.error('Deposit error:', err);
      if (err.response?.data?.message) {
        setDepositError(err.response.data.message);
      } else {
        setDepositError('Failed to submit deposit request. Please try again.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const closeSuccessModal = () => {
    setShowSuccessModal(false);
    setSuccessData(null);
  };



  if (loading) {
    return <div className="loading">Loading wallet data...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="wallet-container">
      <div className="wallet-header">
        <h1>Credit Wallet</h1>
        <p>Manage your credit wallet balance and transaction history</p>
      </div>

      <div className="wallet-balance-card">
        <div className="balance-display">
          <h2>Current Balance</h2>
          <div className="balance-amount">
            <span className="currency">â‚±</span>
            <span className="amount">{creditBalance.toLocaleString()}</span>
          </div>
        </div>
      </div>

      <div className="transactions-section">
        <h3>Transaction History</h3>
        {transactions.length === 0 ? (
          <div className="no-transactions">
            <p>No transactions found.</p>
          </div>
        ) : (
          <div className="transactions-list">
            {transactions.map((transaction, index) => (
              <div key={index} className="transaction-item">
                <div className="transaction-info">
                  <div className="transaction-type">
                    <span className={`type-badge ${transaction.type}`}>
                      {transaction.type}
                    </span>
                  </div>
                  <div className="transaction-details">
                    <p className="transaction-amount">
                      <span className={transaction.type === 'credit' ? 'positive' : 'negative'}>
                        {transaction.type === 'credit' ? '+' : '-'}â‚±{transaction.amount?.toLocaleString()}
                      </span>
                    </p>
                    <p className="transaction-date">
                      {new Date(transaction.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                {transaction.description && (
                  <p className="transaction-description">{transaction.description}</p>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="wallet-info-section">
        <h2>Credit Wallet Information</h2>
        <div className="info-grid">
          <div className="info-card">
            <h4>ðŸ’³ Credit Wallet</h4>
            <p>Your main wallet for activating investment contracts. Deposit funds here to start investing.</p>
          </div>

          <div className="info-card">
            <h4>ðŸ“Š How to Use</h4>
            <p>1. Deposit funds to your credit wallet<br/>2. Go to Dashboard to activate contracts<br/>3. Earn returns on your investments</p>
          </div>

          <div className="info-card">
            <h4>ðŸ”’ Security</h4>
            <p>Your funds are secure and can only be used for contract activation or withdrawal by you.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Wallet;
