import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { Card, Table, InfoBox } from '../components/UI';
import './Withdrawal.css';

const Withdrawal = () => {
  const { user } = useAuth();
  const [walletBalances, setWalletBalances] = useState({
    passive: 0,
    bonus: 0
  });
  const [withdrawalForm, setWithdrawalForm] = useState({
    walletType: 'passive',
    amount: '',
    payoutMethod: 'GCash',
    accountDetails: ''
  });
  const [withdrawalHistory, setWithdrawalHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [encashmentStatus, setEncashmentStatus] = useState({
    isAllowed: true,
    message: '',
    settings: {
      startTime: '11:00',
      endTime: '15:00',
      isEnabled: true,
      overrideActive: false,
      overrideExpiry: null
    }
  });

  useEffect(() => {
    fetchWalletBalances();
    fetchWithdrawalHistory();
    fetchEncashmentStatus();

    // Check encashment status every minute
    const statusInterval = setInterval(fetchEncashmentStatus, 60000);
    return () => clearInterval(statusInterval);
  }, []);

  const fetchWalletBalances = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/wallet/balances', {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });
      if (res.data.success) {
        const { passiveWallet, bonusWallet } = res.data.data;
        setWalletBalances({
          passive: passiveWallet || 0,
          bonus: bonusWallet || 0
        });
        console.log('Withdrawal page wallet balances updated:', { passiveWallet, bonusWallet });
      }
    } catch (error) {
      console.error('Failed to fetch wallet balances:', error);
      setError('Failed to fetch wallet balances');
    }
  };

  const fetchWithdrawalHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/withdrawals', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWithdrawalHistory(res.data.data || []);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch withdrawal history');
      setLoading(false);
    }
  };

  const fetchEncashmentStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/admin/encashment-settings', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setEncashmentStatus(res.data.data);
      }
    } catch (error) {
      console.error('Failed to fetch encashment status:', error);
      // If status fetch fails, default to allowing withdrawals
      setEncashmentStatus({
        passiveWallet: {
          startTime: '11:00',
          endTime: '15:00',
          isEnabled: true,
          allowedDays: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
          overrideActive: false,
          overrideExpiry: null
        },
        directBonusWallet: {
          startTime: '11:00',
          endTime: '15:00',
          isEnabled: true,
          allowedDays: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
          overrideActive: false,
          overrideExpiry: null
        }
      });
    }
  };



  const checkBonusWalletDay = () => {
    const today = new Date().getDay(); // 0 = Sunday, 1 = Monday, etc.
    const allowedDays = [2, 4, 6]; // Tuesday, Thursday, Saturday
    return allowedDays.includes(today);
  };

  const getMinimumAmount = () => {
    return withdrawalForm.walletType === 'passive' ? 300 : 500;
  };

  const validateWithdrawal = () => {
    const amount = parseFloat(withdrawalForm.amount);
    const minAmount = getMinimumAmount();
    const balance = walletBalances[withdrawalForm.walletType];

    // Check wallet-specific encashment status first
    const walletEncashmentStatus = withdrawalForm.walletType === 'passive' 
      ? encashmentStatus.passiveWallet 
      : encashmentStatus.directBonusWallet;

    if (!walletEncashmentStatus?.isAllowed) {
      return walletEncashmentStatus?.message || `${withdrawalForm.walletType === 'passive' ? 'Passive' : 'Direct bonus'} wallet withdrawals are currently not allowed`;
    }

    if (withdrawalForm.walletType === 'bonus' && !checkBonusWalletDay()) {
      return 'Bonus wallet withdrawals are only allowed on Tuesday, Thursday, and Saturday';
    }

    if (!amount || amount <= 0) {
      return 'Please enter a valid amount';
    }

    if (amount < minAmount) {
      return `Minimum withdrawal amount for ${withdrawalForm.walletType} wallet is â‚±${minAmount}`;
    }

    if (amount > balance) {
      return 'Insufficient wallet balance';
    }

    if (!withdrawalForm.payoutMethod) {
      return 'Please select a payout method';
    }

    if (!withdrawalForm.accountDetails.trim()) {
      return 'Please provide account details';
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    setSuccess('');

    const validationError = validateWithdrawal();
    if (validationError) {
      setError(validationError);
      setSubmitting(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/v1/withdrawals', {
        walletType: withdrawalForm.walletType,
        amount: parseFloat(withdrawalForm.amount),
        payoutMethod: withdrawalForm.payoutMethod,
        accountDetails: withdrawalForm.accountDetails
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSuccess('Withdrawal request submitted successfully!');
      setWithdrawalForm({
        walletType: 'passive',
        amount: '',
        payoutMethod: 'GCash',
        accountDetails: ''
      });
      fetchWalletBalances();
      fetchWithdrawalHistory();
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to submit withdrawal request');
    } finally {
      setSubmitting(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'success';
      case 'pending': return 'warning';
      case 'failed': return 'danger';
      case 'cancelled': return 'secondary';
      default: return 'secondary';
    }
  };

  if (loading) {
    return <div className="loading">Loading withdrawal data...</div>;
  }

  return (
    <div className="withdrawal-container">
      <div className="withdrawal-header">
        <h1>Withdrawal Request</h1>
        <p>Manage your wallet withdrawals and track your requests</p>
      </div>

      {/* Wallet-Specific Encashment Status */}
      <div className="encashment-status-grid">
        {/* Passive Wallet Encashment */}
        <InfoBox 
          icon={encashmentStatus.passiveWallet?.isAllowed ? "fas fa-check-circle" : "fas fa-exclamation-triangle"}
          title="Passive Wallet Encashment"
          variant={encashmentStatus.passiveWallet?.isAllowed ? "success" : "warning"}
          className="wallet-encashment-status"
        >
          {encashmentStatus.passiveWallet?.message || (
            encashmentStatus.passiveWallet?.isAllowed 
              ? (
                  <div>
                    <div>Withdrawals allowed</div>
                    <div style={{fontSize: '0.9em', marginTop: '0.5rem', color: '#b0b0b0'}}>
                      Time: {encashmentStatus.passiveWallet?.settings?.startTime} - {encashmentStatus.passiveWallet?.settings?.endTime}
                      {encashmentStatus.passiveWallet?.settings?.allowedDays && (
                        <div>Days: {encashmentStatus.passiveWallet.settings.allowedDays.map(day => day.charAt(0).toUpperCase() + day.slice(1, 3)).join(', ')}</div>
                      )}
                    </div>
                  </div>
                )
              : 'Passive wallet withdrawals not allowed'
          )}
        </InfoBox>

        {/* Direct Bonus Wallet Encashment */}
        <InfoBox 
          icon={encashmentStatus.directBonusWallet?.isAllowed ? "fas fa-check-circle" : "fas fa-exclamation-triangle"}
          title="Direct Bonus Wallet Encashment"
          variant={encashmentStatus.directBonusWallet?.isAllowed ? "success" : "warning"}
          className="wallet-encashment-status"
        >
          {encashmentStatus.directBonusWallet?.message || (
            encashmentStatus.directBonusWallet?.isAllowed 
              ? (
                  <div>
                    <div>Withdrawals allowed</div>
                    <div style={{fontSize: '0.9em', marginTop: '0.5rem', color: '#b0b0b0'}}>
                      Time: {encashmentStatus.directBonusWallet?.settings?.startTime} - {encashmentStatus.directBonusWallet?.settings?.endTime}
                      {encashmentStatus.directBonusWallet?.settings?.allowedDays && (
                        <div>Days: {encashmentStatus.directBonusWallet.settings.allowedDays.map(day => day.charAt(0).toUpperCase() + day.slice(1, 3)).join(', ')}</div>
                      )}
                    </div>
                  </div>
                )
              : 'Direct bonus wallet withdrawals not allowed'
          )}
        </InfoBox>
      </div>

      {/* Wallet Balances */}
      <div className="wallet-balances-grid">
        <Card variant="wallet" className="passive-wallet">
          <div className="wallet-header">
            <h3>Passive Wallet</h3>
            <i className="fas fa-wallet"></i>
          </div>
          <div className="balance-display">
            <span className="currency">â‚±</span>
            <span className="amount">{walletBalances.passive.toLocaleString()}</span>
          </div>
          <p className="wallet-info">Minimum withdrawal: â‚±300</p>
        </Card>

        <Card variant="wallet" className="bonus-wallet">
          <div className="wallet-header">
            <h3>Bonus Wallet</h3>
            <i className="fas fa-gift"></i>
          </div>
          <div className="balance-display">
            <span className="currency">â‚±</span>
            <span className="amount">{walletBalances.bonus.toLocaleString()}</span>
          </div>
          <p className="wallet-info">Minimum: â‚±500 (Tue/Thu/Sat only)</p>
        </Card>
      </div>

      {/* Withdrawal Form */}
      <Card variant="withdrawal" className="withdrawal-form-card">
        <div className="card-header">
          <h2>Request Withdrawal</h2>
          <p>Submit a new withdrawal request from your available wallets</p>
        </div>

        <form onSubmit={handleSubmit} className="withdrawal-form">
          <div className="form-group">
            <label htmlFor="walletType">
              <i className="fas fa-wallet"></i>
              Select Wallet
            </label>
            <select
              id="walletType"
              value={withdrawalForm.walletType}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, walletType: e.target.value})}
              required
            >
              <option value="passive">Passive Wallet (â‚±{walletBalances.passive.toLocaleString()})</option>
              <option value="bonus">Bonus Wallet (â‚±{walletBalances.bonus.toLocaleString()})</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="amount">
              <i className="fas fa-money-bill-wave"></i>
              Withdrawal Amount (â‚±)
            </label>
            <input
              type="number"
              id="amount"
              value={withdrawalForm.amount}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, amount: e.target.value})}
              placeholder={`Minimum â‚±${getMinimumAmount()}`}
              min={getMinimumAmount()}
              max={walletBalances[withdrawalForm.walletType]}
              step="1"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="payoutMethod">
              <i className="fas fa-credit-card"></i>
              Payout Method
            </label>
            <select
              id="payoutMethod"
              value={withdrawalForm.payoutMethod}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, payoutMethod: e.target.value})}
              required
            >
              <option value="GCash">GCash</option>
              <option value="Maya">Maya</option>
              <option value="GoTyme">GoTyme</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="accountDetails">
              <i className="fas fa-user"></i>
              Account Details
            </label>
            <input
              type="text"
              id="accountDetails"
              value={withdrawalForm.accountDetails}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, accountDetails: e.target.value})}
              placeholder="Enter your account number/mobile number"
              required
            />
          </div>

          {error && (
            <InfoBox variant="error" className="compact">
              {error}
            </InfoBox>
          )}
          {success && (
            <InfoBox variant="success" className="compact">
              {success}
            </InfoBox>
          )}

          <button 
            type="submit" 
            className={`submit-btn ${submitting ? 'submitting' : ''}`}
            disabled={submitting || !(withdrawalForm.walletType === 'passive' ? encashmentStatus.passiveWallet?.isAllowed : encashmentStatus.directBonusWallet?.isAllowed)}
          >
            {submitting ? (
              <>
                <i className="fas fa-spinner fa-spin"></i>
                Processing...
              </>
            ) : (
              <>
                <i className="fas fa-paper-plane"></i>
                Submit Withdrawal Request
              </>
            )}
          </button>
        </form>
      </Card>

      {/* Withdrawal History */}
      <Card variant="earnings" className="withdrawal-history-card">
        <div className="card-header">
          <h2>Withdrawal History</h2>
          <p>Track your withdrawal requests and their status</p>
        </div>

        {withdrawalHistory.length === 0 ? (
          <div className="no-history">
            <div className="no-history-icon">
              <i className="fas fa-history"></i>
            </div>
            <h4>No Withdrawal History</h4>
            <p>You haven't made any withdrawal requests yet.</p>
          </div>
        ) : (
          <Table striped hover>
            <Table.Header>
              <Table.Row>
                <Table.Cell header>Amount</Table.Cell>
                <Table.Cell header>Wallet</Table.Cell>
                <Table.Cell header>Method</Table.Cell>
                <Table.Cell header>Account</Table.Cell>
                <Table.Cell header>Fee</Table.Cell>
                <Table.Cell header>Net Amount</Table.Cell>
                <Table.Cell header>Status</Table.Cell>
                <Table.Cell header>Date</Table.Cell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {withdrawalHistory.map((withdrawal) => (
                <Table.Row key={withdrawal._id}>
                  <Table.Cell>
                    <span className="amount negative">â‚±{withdrawal.amount.toLocaleString()}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className={`wallet-type ${withdrawal.walletType}`}>
                      {withdrawal.walletType.toUpperCase()}
                    </span>
                  </Table.Cell>
                  <Table.Cell>{withdrawal.paymentMethod || withdrawal.payoutMethod}</Table.Cell>
                  <Table.Cell>
                    <span className="account-details">{withdrawal.paymentDetails?.accountNumber || withdrawal.accountDetails}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className="amount negative">â‚±{withdrawal.fee?.toLocaleString() || '0'}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className="amount positive">â‚±{withdrawal.netAmount?.toLocaleString() || withdrawal.amount.toLocaleString()}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className={`status-badge ${withdrawal.status?.toLowerCase()}`}>
                      {withdrawal.status?.toUpperCase()}
                    </span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className="date">{formatDate(withdrawal.createdAt)}</span>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
        )}
      </Card>
    </div>
  );
};

export default Withdrawal;
