import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    totalInvestments: 0,
    activeInvestments: 0,
    totalReturns: 0,
    walletBalance: 0,
    recentInvestments: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch investments
      const investmentsRes = await axios.get('http://localhost:5000/api/v1/investments');
      const investments = investmentsRes.data.data;

      // Fetch wallet data
      const walletRes = await axios.get('http://localhost:5000/api/v1/wallets');
      const wallets = walletRes.data.data;

      // Calculate dashboard metrics
      const totalInvestments = investments.length;
      const activeInvestments = investments.filter(inv => inv.status === 'active').length;
      const totalReturns = investments.reduce((sum, inv) => {
        return sum + (inv.status === 'completed' ? inv.totalReturn : 0);
      }, 0);

      // Get cash wallet balance
      const cashWallet = wallets.find(wallet => wallet.type === 'cash');
      const walletBalance = cashWallet ? cashWallet.balance : 0;

      // Get recent investments (last 5)
      const recentInvestments = investments
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        .slice(0, 5);

      setDashboardData({
        totalInvestments,
        activeInvestments,
        totalReturns,
        walletBalance,
        recentInvestments
      });

      setLoading(false);
    } catch (error) {
      setError('Failed to fetch dashboard data');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome back, {user?.username}!</h1>
        <p>Here's your investment overview</p>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon wallet">ðŸ’°</div>
          <div className="stat-content">
            <h3>â‚±{dashboardData.walletBalance.toLocaleString()}</h3>
            <p>Wallet Balance</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon investments">ðŸ“ˆ</div>
          <div className="stat-content">
            <h3>{dashboardData.totalInvestments}</h3>
            <p>Total Investments</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon active">ðŸ”¥</div>
          <div className="stat-content">
            <h3>{dashboardData.activeInvestments}</h3>
            <p>Active Investments</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon returns">ðŸ’Ž</div>
          <div className="stat-content">
            <h3>â‚±{dashboardData.totalReturns.toLocaleString()}</h3>
            <p>Total Returns</p>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <Link to="/investment" className="action-card">
            <div className="action-icon">ðŸ’°</div>
            <h3>New Investment</h3>
            <p>Start a new investment plan</p>
          </Link>

          <Link to="/wallet" className="action-card">
            <div className="action-icon">ðŸ‘›</div>
            <h3>Manage Wallet</h3>
            <p>View and manage your wallets</p>
          </Link>

          <Link to="/transactions" className="action-card">
            <div className="action-icon">ðŸ“Š</div>
            <h3>View Transactions</h3>
            <p>Check your transaction history</p>
          </Link>
        </div>
      </div>

      {/* Recent Investments */}
      <div className="recent-investments">
        <h2>Recent Investments</h2>

        {dashboardData.recentInvestments.length === 0 ? (
          <div className="no-investments">
            <p>No investments yet. <Link to="/investment">Start your first investment!</Link></p>
          </div>
        ) : (
          <div className="investments-list">
            {dashboardData.recentInvestments.map((investment) => (
              <div key={investment._id} className="investment-item">
                <div className="investment-info">
                  <h4>Investment #{investment._id.slice(-6)}</h4>
                  <p>â‚±{investment.amount.toLocaleString()} â€¢ {investment.package.toUpperCase()}</p>
                </div>

                <div className="investment-status">
                  <span className={`status ${investment.status}`}>
                    {investment.status.toUpperCase()}
                  </span>
                  {investment.status === 'active' && (
                    <p className="days-remaining">
                      {calculateDaysRemaining(investment.endDate)} days left
                    </p>
                  )}
                </div>

                <div className="investment-date">
                  <p>{formatDate(investment.startDate)}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
