import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Withdrawal.css';

const Withdrawal = () => {
  const { user } = useAuth();
  const [walletBalances, setWalletBalances] = useState({
    passive: 0,
    bonus: 0
  });
  const [withdrawalForm, setWithdrawalForm] = useState({
    walletType: 'passive',
    amount: '',
    payoutMethod: 'GCash',
    accountDetails: ''
  });
  const [withdrawalHistory, setWithdrawalHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [timeRestriction, setTimeRestriction] = useState({
    isWithinHours: false,
    currentTime: '',
    allowedHours: '11:00 AM - 3:00 PM'
  });

  useEffect(() => {
    fetchWalletBalances();
    fetchWithdrawalHistory();
    checkTimeRestriction();

    // Check time restriction every minute
    const timeInterval = setInterval(checkTimeRestriction, 60000);
    return () => clearInterval(timeInterval);
  }, []);

  const fetchWalletBalances = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/wallets', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWalletBalances({
        passive: res.data.data.passive || 0,
        bonus: res.data.data.bonus || 0
      });
    } catch (error) {
      console.error('Failed to fetch wallet balances');
    }
  };

  const fetchWithdrawalHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/transactions?type=withdrawal', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWithdrawalHistory(res.data.data || []);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch withdrawal history');
      setLoading(false);
    }
  };

  const checkTimeRestriction = () => {
    const now = new Date();
    const currentHour = now.getHours();
    const isWithinHours = currentHour >= 11 && currentHour < 15; // 11 AM to 3 PM

    setTimeRestriction({
      isWithinHours,
      currentTime: now.toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true
      }),
      allowedHours: '11:00 AM - 3:00 PM'
    });
  };

  const checkBonusWalletDay = () => {
    const today = new Date().getDay(); // 0 = Sunday, 1 = Monday, etc.
    const allowedDays = [2, 4, 6]; // Tuesday, Thursday, Saturday
    return allowedDays.includes(today);
  };

  const getMinimumAmount = () => {
    return withdrawalForm.walletType === 'passive' ? 300 : 500;
  };

  const validateWithdrawal = () => {
    const amount = parseFloat(withdrawalForm.amount);
    const minAmount = getMinimumAmount();
    const balance = walletBalances[withdrawalForm.walletType];

    if (!timeRestriction.isWithinHours) {
      return 'Withdrawals are only allowed between 11:00 AM - 3:00 PM';
    }

    if (withdrawalForm.walletType === 'bonus' && !checkBonusWalletDay()) {
      return 'Bonus wallet withdrawals are only allowed on Tuesday, Thursday, and Saturday';
    }

    if (!amount || amount <= 0) {
      return 'Please enter a valid amount';
    }

    if (amount < minAmount) {
      return `Minimum withdrawal amount for ${withdrawalForm.walletType} wallet is ₱${minAmount}`;
    }

    if (amount > balance) {
      return 'Insufficient wallet balance';
    }

    if (!withdrawalForm.accountDetails.trim()) {
      return 'Please provide account details';
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    setSuccess('');

    const validationError = validateWithdrawal();
    if (validationError) {
      setError(validationError);
      setSubmitting(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/v1/transactions/withdrawal', {
        walletType: withdrawalForm.walletType,
        amount: parseFloat(withdrawalForm.amount),
        payoutMethod: withdrawalForm.payoutMethod,
        accountDetails: withdrawalForm.accountDetails
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSuccess('Withdrawal request submitted successfully!');
      setWithdrawalForm({
        walletType: 'passive',
        amount: '',
        payoutMethod: 'GCash',
        accountDetails: ''
      });
      fetchWalletBalances();
      fetchWithdrawalHistory();
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to submit withdrawal request');
    } finally {
      setSubmitting(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'success';
      case 'pending': return 'warning';
      case 'failed': return 'danger';
      case 'cancelled': return 'secondary';
      default: return 'secondary';
    }
  };

  if (loading) {
    return <div className="loading">Loading withdrawal data...</div>;
  }

  return (
    <div className="withdrawal-container">
      <h1>Withdrawal Request</h1>

      {/* Time Status */}
      <div className={`time-status ${timeRestriction.isWithinHours ? 'allowed' : 'restricted'}`}>
        <h3>Current Time: {timeRestriction.currentTime}</h3>
        <p>
          {timeRestriction.isWithinHours 
            ? '✅ Withdrawals are currently allowed' 
            : '❌ Withdrawals are only allowed between ' + timeRestriction.allowedHours
          }
        </p>
      </div>

      {/* Wallet Balances */}
      <div className="wallet-balances">
        <div className="balance-card passive">
          <h3>Passive Wallet</h3>
          <p className="balance">₱{walletBalances.passive.toLocaleString()}</p>
          <p className="min-amount">Min: ₱300</p>
        </div>
        <div className="balance-card bonus">
          <h3>Bonus Wallet</h3>
          <p className="balance">₱{walletBalances.bonus.toLocaleString()}</p>
          <p className="min-amount">Min: ₱500 (Tue/Thu/Sat only)</p>
        </div>
      </div>

      {/* Withdrawal Form */}
      <div className="withdrawal-form-section">
        <h2>Request Withdrawal</h2>

        <form onSubmit={handleSubmit} className="withdrawal-form">
          <div className="form-group">
            <label htmlFor="walletType">Select Wallet</label>
            <select
              id="walletType"
              value={withdrawalForm.walletType}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, walletType: e.target.value})}
              required
            >
              <option value="passive">Passive Wallet (₱{walletBalances.passive.toLocaleString()})</option>
              <option value="bonus">Bonus Wallet (₱{walletBalances.bonus.toLocaleString()})</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="amount">Withdrawal Amount (₱)</label>
            <input
              type="number"
              id="amount"
              value={withdrawalForm.amount}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, amount: e.target.value})}
              placeholder={`Minimum ₱${getMinimumAmount()}`}
              min={getMinimumAmount()}
              max={walletBalances[withdrawalForm.walletType]}
              step="1"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="payoutMethod">Payout Method</label>
            <select
              id="payoutMethod"
              value={withdrawalForm.payoutMethod}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, payoutMethod: e.target.value})}
              required
            >
              <option value="GCash">GCash</option>
              <option value="Maya">Maya</option>
              <option value="GoTyme">GoTyme</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="accountDetails">Account Details</label>
            <input
              type="text"
              id="accountDetails"
              value={withdrawalForm.accountDetails}
              onChange={(e) => setWithdrawalForm({...withdrawalForm, accountDetails: e.target.value})}
              placeholder="Enter your account number/mobile number"
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <button 
            type="submit" 
            className="submit-btn"
            disabled={submitting || !timeRestriction.isWithinHours}
          >
            {submitting ? 'Processing...' : 'Submit Withdrawal Request'}
          </button>
        </form>
      </div>

      {/* Withdrawal History */}
      <div className="withdrawal-history-section">
        <h2>Withdrawal History</h2>

        {withdrawalHistory.length === 0 ? (
          <div className="no-history">
            <p>No withdrawal requests found.</p>
          </div>
        ) : (
          <div className="history-grid">
            {withdrawalHistory.map((withdrawal) => (
              <div key={withdrawal._id} className="history-card">
                <div className="history-header">
                  <h3>₱{withdrawal.amount.toLocaleString()}</h3>
                  <span className={`status ${getStatusColor(withdrawal.status)}`}>
                    {withdrawal.status.toUpperCase()}
                  </span>
                </div>

                <div className="history-details">
                  <div className="detail-row">
                    <span>Wallet:</span>
                    <span>{withdrawal.walletType.toUpperCase()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Method:</span>
                    <span>{withdrawal.payoutMethod}</span>
                  </div>
                  <div className="detail-row">
                    <span>Account:</span>
                    <span>{withdrawal.accountDetails}</span>
                  </div>
                  <div className="detail-row">
                    <span>Fee:</span>
                    <span>₱{withdrawal.fee?.toLocaleString() || '0'}</span>
                  </div>
                  <div className="detail-row">
                    <span>Net Amount:</span>
                    <span>₱{withdrawal.netAmount?.toLocaleString() || withdrawal.amount.toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Date:</span>
                    <span>{formatDate(withdrawal.createdAt)}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Withdrawal;
