import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, InfoBox } from '../components/UI';
import './ActiveContracts.css';

const ActiveContracts = () => {
  const [contracts, setContracts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchActiveContracts();
    // Set up interval to update countdown timers every minute
    const interval = setInterval(() => {
      setContracts(prevContracts => [...prevContracts]); // Force re-render for countdown updates
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  const fetchActiveContracts = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/investments/active', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.status === 'success') {
        setContracts(res.data.data || []);
      }
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch active contracts:', error);
      setError('Failed to load contracts');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getNextPayoutCountdown = (contract) => {
    if (!contract.nextPayout) return 'No pending payouts';

    if (contract.nextPayout.status === 'due') {
      return 'Payout Due!';
    }

    if (contract.nextPayout.timeLeft) {
      return contract.nextPayout.timeLeft.formatted;
    }

    return 'Calculating...';
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'active':
        return '#4CAF50';
      case 'completed':
        return '#2196F3';
      default:
        return '#757575';
    }
  };

  const getProgressPercentage = (progress) => {
    if (!progress) return 0;
    return (progress.completed / progress.total) * 100;
  };

  if (loading) {
    return (
      <div className="active-contracts">
        <div className="loading">Loading contracts...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="active-contracts">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="active-contracts">
      <div className="page-header">
        <h1>Active Contracts</h1>
        <p>Track your investment contracts and earnings schedule</p>
      </div>

      {contracts.length === 0 ? (
        <Card className="no-contracts">
          <div className="no-contracts-content">
            <h3>No Active Contracts</h3>
            <p>You don't have any active contracts yet. Start investing to see your contracts here.</p>
          </div>
        </Card>
      ) : (
        <div className="contracts-grid">
          {contracts.map((contract) => (
            <Card key={contract.id} className="contract-card">
              <div className="contract-header">
                <div className="contract-amount">
                  â‚±{contract.amount.toLocaleString()}
                </div>
                <div 
                  className="contract-status"
                  style={{ color: getStatusColor(contract.status) }}
                >
                  {contract.status}
                </div>
              </div>

              <div className="contract-details">
                <div className="detail-row">
                  <span className="label">Start Date:</span>
                  <span className="value">{formatDate(contract.startDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Maturity Date:</span>
                  <span className="value">{formatDate(contract.maturityDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Days Left:</span>
                  <span className="value">{contract.daysLeft} days</span>
                </div>
              </div>

              <div className="progress-section">
                <div className="progress-header">
                  <span>Progress: {contract.progress?.completed || 0}/{contract.progress?.total || 5} payouts</span>
                  <span>{contract.progress?.percentage || 0}%</span>
                </div>
                <div className="progress-bar">
                  <div 
                    className="progress-fill"
                    style={{ width: `${getProgressPercentage(contract.progress)}%` }}
                  ></div>
                </div>
              </div>

              {contract.status === 'ACTIVE' && (
                <div className="next-payout">
                  <div className="next-payout-header">Next Payout</div>
                  {contract.nextPayout && (
                    <>
                      <div className="payout-amount">
                        â‚±{contract.nextPayout.payoutAmount?.toLocaleString() || '0'}
                      </div>
                      <div className="countdown">
                        {getNextPayoutCountdown(contract)}
                      </div>
                    </>
                  )}
                </div>
              )}

              <div className="payout-schedule">
                <h4>Payout Schedule</h4>
                <div className="schedule-list">
                  {contract.payoutSchedule?.map((payout, index) => (
                    <div 
                      key={index} 
                      className={`schedule-item ${payout.status}`}
                    >
                      <div className="schedule-date">
                        Day {(index + 1) * 3}
                      </div>
                      <div className="schedule-amount">
                        â‚±{payout.payoutAmount.toLocaleString()}
                      </div>
                      <div className="schedule-status">
                        {payout.status === 'completed' ? 'âœ“' : 'â—‹'}
                      </div>
                    </div>
                  )) || []}
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}

      <div className="contracts-summary">
        <div className="summary-grid">
          <InfoBox
            title="Total Active Contracts"
            value={contracts.filter(c => c.status === 'ACTIVE').length}
            icon="ðŸ“Š"
          />
          <InfoBox
            title="Total Investment"
            value={`â‚±${contracts.reduce((sum, c) => sum + c.amount, 0).toLocaleString()}`}
            icon="ðŸ’°"
          />
          <InfoBox
            title="Completed Contracts"
            value={contracts.filter(c => c.status === 'COMPLETED').length}
            icon="âœ…"
          />
          <InfoBox
            title="Total Payouts Received"
            value={contracts.reduce((sum, c) => sum + (c.totalPayouts || 0), 0)}
            icon="ðŸŽ¯"
          />
        </div>
      </div>
    </div>
  );
};

export default ActiveContracts;
