import React, { useState } from 'react';

const ReferralLink = ({ link }) => {
  const [copied, setCopied] = useState(false);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(link);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="w-full flex flex-col items-center mt-4">
      <h3 className="text-green-400 font-semibold mb-2">Your Referral Link</h3>
      <div className="bg-gray-800 p-3 rounded-md shadow-sm flex justify-between items-center text-sm text-gray-200 w-3/4">
        <span className="truncate">{link}</span>
        <button 
          onClick={copyToClipboard}
          className="ml-2 bg-green-500 hover:bg-green-600 text-white px-2 py-1 rounded-md text-xs transition"
        >
          {copied ? "Copied!" : "Copy"}
        </button>
      </div>
    </div>
  );
};

export default ReferralLink;
