import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, InfoBox } from '../components/UI';
import './ActiveContracts.css';

const ActiveContracts = () => {
  const [contracts, setContracts] = useState([]);
  const [summary, setSummary] = useState({
    totalActiveContracts: 0,
    totalInvestment: 0,
    completedContracts: 0,
    totalPayoutsReceived: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchActiveContracts();
    fetchSummary();
    // Set up interval to update countdown timers every minute
    const interval = setInterval(() => {
      setContracts(prevContracts => [...prevContracts]); // Force re-render for countdown updates
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  const fetchActiveContracts = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/investments/active', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.status === 'success') {
        setContracts(res.data.data || []);
      }
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch active contracts:', error);
      setError('Failed to load contracts');
      setLoading(false);
    }
  };

  const fetchSummary = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/contracts/summary', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        setSummary(res.data.data);
      }
    } catch (error) {
      console.error('Failed to fetch investment summary:', error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getNextPayoutCountdown = (contract) => {
    if (!contract.nextPayout) return 'No pending payouts';

    if (contract.nextPayout.status === 'due') {
      return 'Payout Due!';
    }

    if (contract.nextPayout.timeLeft) {
      return contract.nextPayout.timeLeft.formatted;
    }

    return 'Calculating...';
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'active':
        return '#4CAF50';
      case 'completed':
        return '#2196F3';
      default:
        return '#757575';
    }
  };

  const getProgressPercentage = (contract) => {
    if (!contract.startDate || !contract.maturityDate) return 0;

    const now = new Date();
    const startDate = new Date(contract.startDate);
    const maturityDate = new Date(contract.maturityDate);
    const totalDuration = maturityDate.getTime() - startDate.getTime();
    const elapsed = now.getTime() - startDate.getTime();

    const percentage = Math.min(100, Math.max(0, (elapsed / totalDuration) * 100));
    return Math.round(percentage);
  };

  if (loading) {
    return (
      <div className="active-contracts">
        <div className="loading">Loading contracts...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="active-contracts">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="active-contracts">
      <div className="page-header">
        <h1>Active Contracts</h1>
        <p>Track your investment contracts and earnings schedule</p>
      </div>

      {contracts.length === 0 ? (
        <Card className="no-contracts">
          <div className="no-contracts-content">
            <h3>No Active Contracts</h3>
            <p>You don't have any active contracts yet. Start investing to see your contracts here.</p>
          </div>
        </Card>
      ) : (
        <div className="contracts-grid">
          {contracts.map((contract) => (
            <Card key={contract.id} className="contract-card">
              <div className="contract-header">
                <div className="contract-amount">
                  â‚±{contract.amount.toLocaleString()}
                </div>
                <div 
                  className="contract-status"
                  style={{ color: getStatusColor(contract.status) }}
                >
                  {contract.status}
                </div>
              </div>

              <div className="contract-details">
                <div className="detail-row">
                  <span className="label">Start Date:</span>
                  <span className="value">{formatDate(contract.startDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Maturity Date:</span>
                  <span className="value">{formatDate(contract.maturityDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Days Left:</span>
                  <span className="value">{contract.daysLeft} days</span>
                </div>
              </div>

              <div className="progress-section">
                <div className="progress-header">
                  <span>Contract Progress</span>
                  <span>{getProgressPercentage(contract)}%</span>
                </div>
                <div className="progress-bar">
                  <div 
                    className="progress-fill"
                    style={{ width: `${getProgressPercentage(contract)}%` }}
                  ></div>
                </div>
              </div>

              <div className="expected-return">
                <div className="return-header">Expected Return</div>
                <div className="return-amount">
                  â‚±{(contract.amount * 4).toLocaleString()}
                </div>
                <div className="return-breakdown">
                  <span>â‚±{(contract.amount * 3).toLocaleString()} profit + â‚±{contract.amount.toLocaleString()} capital</span>
                </div>
              </div>

              {contract.status === 'ACTIVE' && contract.daysLeft === 0 && (
                <div className="maturity-notice">
                  <div className="maturity-header">Contract Matured!</div>
                  <div className="maturity-amount">
                    â‚±{(contract.amount * 4).toLocaleString()}
                  </div>
                  <div className="maturity-text">Ready for payout</div>
                </div>
              )}
            </Card>
          ))}
        </div>
      )}

      <div className="contracts-summary">
        <div className="summary-grid">
          <InfoBox
            title="Total Active Contracts"
            icon="ðŸ“Š"
          >
            {summary.totalActiveContracts || 0}
          </InfoBox>
          <InfoBox
            title="Total Investment"
            icon="ðŸ’°"
          >
            â‚±{(summary.totalInvestment || 0).toLocaleString()}
          </InfoBox>
          <InfoBox
            title="Completed Contracts"
            icon="âœ…"
          >
            {summary.completedContracts || 0}
          </InfoBox>
          <InfoBox
            title="Total Payouts Received"
            icon="ðŸŽ¯"
          >
            â‚±{(summary.totalPayoutsReceived || 0).toLocaleString()}
          </InfoBox>
        </div>
      </div>
    </div>
  );
};

export default ActiveContracts;
