import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Investment.css';

const Investment = () => {
  const { user } = useAuth();
  const [investments, setInvestments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [depositAmount, setDepositAmount] = useState('');
  const [isDepositing, setIsDepositing] = useState(false);

  useEffect(() => {
    fetchInvestments();
  }, []);

  const fetchInvestments = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/v1/investments');
      setInvestments(res.data.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch investments');
      setLoading(false);
    }
  };

  const handleDeposit = async (e) => {
    e.preventDefault();
    setIsDepositing(true);
    setError('');
    setSuccess('');

    try {
      const amount = parseFloat(depositAmount);

      if (!amount || amount <= 0) {
        setError('Please enter a valid amount');
        setIsDepositing(false);
        return;
      }

      const res = await axios.post('http://localhost:5000/api/v1/investments/deposit', {
        amount,
        package: 'basic'
      });

      setSuccess(`Investment of â‚±${amount.toLocaleString()} created successfully!`);
      setDepositAmount('');
      fetchInvestments(); // Refresh investments list
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to create investment');
    } finally {
      setIsDepositing(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (loading) {
    return <div className="loading">Loading investments...</div>;
  }

  return (
    <div className="investment-container">
      <h1>Investment Dashboard</h1>

      {/* Deposit Form */}
      <div className="deposit-section">
        <h2>Make New Investment</h2>
        <div className="deposit-info">
          <p><strong>Minimum Capital:</strong> â‚±1,000</p>
          <p><strong>Maximum Capital:</strong> â‚±10,000</p>
          <p><strong>Investment Period:</strong> 15 days</p>
          <p><strong>Expected Return:</strong> 10%</p>
        </div>

        <form onSubmit={handleDeposit} className="deposit-form">
          <div className="form-group">
            <label htmlFor="amount">Investment Amount (â‚±)</label>
            <input
              type="number"
              id="amount"
              value={depositAmount}
              onChange={(e) => setDepositAmount(e.target.value)}
              placeholder="Enter amount (â‚±1,000 - â‚±10,000)"
              min="1000"
              max="10000"
              step="100"
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <button 
            type="submit" 
            className="deposit-btn"
            disabled={isDepositing}
          >
            {isDepositing ? 'Processing...' : 'Invest Now'}
          </button>
        </form>
      </div>

      {/* Active Investments */}
      <div className="investments-section">
        <h2>Your Investments</h2>

        {investments.length === 0 ? (
          <div className="no-investments">
            <p>No investments found. Start your first investment above!</p>
          </div>
        ) : (
          <div className="investments-grid">
            {investments.map((investment) => (
              <div key={investment._id} className="investment-card">
                <div className="investment-header">
                  <h3>Investment #{investment._id.slice(-6)}</h3>
                  <span className={`status ${investment.status}`}>
                    {investment.status.toUpperCase()}
                  </span>
                </div>

                <div className="investment-details">
                  <div className="detail-row">
                    <span>Capital:</span>
                    <span>â‚±{investment.amount.toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Package:</span>
                    <span>{investment.package.toUpperCase()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Start Date:</span>
                    <span>{formatDate(investment.startDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Maturity Date:</span>
                    <span>{formatDate(investment.endDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Days Remaining:</span>
                    <span>{calculateDaysRemaining(investment.endDate)} days</span>
                  </div>
                  <div className="detail-row">
                    <span>Expected Return:</span>
                    <span>â‚±{(investment.amount * investment.returnRate).toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Cycle:</span>
                    <span>{investment.cycle}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Investment;
