import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Investment.css';

const Investment = () => {
  const { user } = useAuth();
  const [investments, setInvestments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activationAmount, setActivationAmount] = useState('');
  const [isActivating, setIsActivating] = useState(false);
  const [walletBalance, setWalletBalance] = useState({ credit: 0 });

  useEffect(() => {
    fetchInvestments();
    fetchWalletBalance();
  }, []);

  const fetchInvestments = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/investments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setInvestments(res.data.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch investments');
      setLoading(false);
    }
  };

  const fetchWalletBalance = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/wallets/balance', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWalletBalance(res.data.data);
    } catch (error) {
      console.error('Failed to fetch wallet balance');
    }
  };

  const handleActivation = async (e) => {
    e.preventDefault();
    setIsActivating(true);
    setError('');
    setSuccess('');

    try {
      const amount = parseFloat(activationAmount);

      if (!amount || amount <= 0) {
        setError('Please enter a valid amount');
        setIsActivating(false);
        return;
      }

      if (amount < 1000) {
        setError('Minimum activation amount is ₱1,000');
        setIsActivating(false);
        return;
      }

      if (amount > walletBalance.credit) {
        setError('Insufficient Credit Wallet balance');
        setIsActivating(false);
        return;
      }

      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/v1/investments/activate', {
        amount
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSuccess(`Contract activated successfully! Amount: ₱${amount.toLocaleString()}`);
      setActivationAmount('');
      fetchInvestments();
      fetchWalletBalance();
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to activate contract');
    } finally {
      setIsActivating(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (loading) {
    return <div className="loading">Loading investments...</div>;
  }

  return (
    <div className="investment-container">
      <h1>Investment Dashboard</h1>

      {/* Deposit Form */}
      <div className="deposit-section">
        <h2>Make New Investment</h2>
        <div className="deposit-info">
          <p><strong>Minimum Capital:</strong> ₱1,000</p>
          <p><strong>Maximum Capital:</strong> ₱10,000</p>
          <p><strong>Investment Period:</strong> 15 days</p>
          <p><strong>Expected Return:</strong> 10%</p>
        </div>

        <form onSubmit={handleDeposit} className="deposit-form">
          <div className="form-group">
            <label htmlFor="amount">Investment Amount (₱)</label>
            <input
              type="number"
              id="amount"
              value={depositAmount}
              onChange={(e) => setDepositAmount(e.target.value)}
              placeholder="Enter amount (₱1,000 - ₱10,000)"
              min="1000"
              max="10000"
              step="100"
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <button 
            type="submit" 
            className="deposit-btn"
            disabled={isDepositing}
          >
            {isDepositing ? 'Processing...' : 'Invest Now'}
          </button>
        </form>
      </div>

      {/* Active Investments */}
      <div className="investments-section">
        <h2>Your Investments</h2>

        {investments.length === 0 ? (
          <div className="no-investments">
            <p>No investments found. Start your first investment above!</p>
          </div>
        ) : (
          <div className="investments-grid">
            {investments.map((investment) => (
              <div key={investment._id} className="investment-card">
                <div className="investment-header">
                  <h3>Investment #{investment._id.slice(-6)}</h3>
                  <span className={`status ${investment.status}`}>
                    {investment.status.toUpperCase()}
                  </span>
                </div>

                <div className="investment-details">
                  <div className="detail-row">
                    <span>Capital:</span>
                    <span>₱{investment.amount.toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Package:</span>
                    <span>{investment.package.toUpperCase()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Start Date:</span>
                    <span>{formatDate(investment.startDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Maturity Date:</span>
                    <span>{formatDate(investment.endDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Days Remaining:</span>
                    <span>{calculateDaysRemaining(investment.endDate)} days</span>
                  </div>
                  <div className="detail-row">
                    <span>Expected Return:</span>
                    <span>₱{(investment.amount * investment.returnRate).toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Cycle:</span>
                    <span>{investment.cycle}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Investment;
