import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import SuccessModal from '../components/SuccessModal';
import './Investment.css';

const Investment = () => {
  const [investments, setInvestments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activationAmount, setActivationAmount] = useState('');
  const [isActivating, setIsActivating] = useState(false);
  const [walletBalance, setWalletBalance] = useState({ credit: 0 });
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [successData, setSuccessData] = useState(null);
  const [validationError, setValidationError] = useState('');

  useEffect(() => {
    fetchInvestments();
    fetchWalletBalance();
  }, []);

  const fetchInvestments = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/investments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setInvestments(res.data.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch investments');
      setLoading(false);
    }
  };

  const fetchWalletBalance = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/v1/wallets/balance', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWalletBalance(res.data.data);
    } catch (error) {
      console.error('Failed to fetch wallet balance');
    }
  };

  const validateAmount = (value) => {
    const amount = parseFloat(value);

    if (!value || value === '') {
      setValidationError('');
      return;
    }

    if (isNaN(amount) || amount <= 0) {
      setValidationError('Please enter a valid amount');
      return;
    }

    if (amount < 1000) {
      setValidationError('Minimum activation is ₱1,000');
      return;
    }

    if (amount > 100000) {
      setValidationError('Maximum activation is ₱100,000');
      return;
    }

    if (amount > walletBalance.credit) {
      setValidationError('Insufficient Credit Wallet balance');
      return;
    }

    setValidationError('');
  };

  const handleAmountChange = (e) => {
    const value = e.target.value;
    setActivationAmount(value);
    validateAmount(value);
  };

  const handleActivation = async (e) => {
    e.preventDefault();
    setIsActivating(true);
    setError('');
    setSuccess('');

    try {
      const amount = parseFloat(activationAmount);

      // Final validation before submission
      if (validationError) {
        setError(validationError);
        setIsActivating(false);
        return;
      }

      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/v1/investments/activate', {
        amount
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Handle success response
      if (res.data.status === 'success') {
        setSuccessData({
          amount: amount,
          updatedBalance: res.data.data.updatedBalance,
          investment: res.data.data.investment
        });
        setShowSuccessModal(true);
        setActivationAmount('');
        setValidationError('');
        fetchInvestments();
        fetchWalletBalance();
      }
    } catch (error) {
      // Handle error response
      if (error.response?.data?.status === 'failed') {
        setError(error.response.data.message);
      } else {
        setError(error.response?.data?.error || 'Failed to activate contract');
      }
    } finally {
      setIsActivating(false);
    }
  };

  const closeSuccessModal = () => {
    setShowSuccessModal(false);
    setSuccessData(null);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (loading) {
    return <div className="loading">Loading investments...</div>;
  }

  return (
    <div className="investment-container">
      <h1>Contract Activation</h1>

      {/* Wallet Balance Display */}
      <div className="wallet-balance">
        <h3>Credit Wallet Balance: ₱{walletBalance.credit?.toLocaleString() || '0'}</h3>
      </div>

      {/* Activation Form */}
      <div className="activation-section">
        <h2>Activate New Contract</h2>
        <div className="activation-rules">
          <p className="rules-text">Minimum activation: ₱1,000 | Maximum activation: ₱100,000</p>
        </div>

        <div className="plan-info">
          <p><strong>Duration:</strong> 15 days</p>
          <p><strong>Payout:</strong> 30% every 3 days</p>
          <p><strong>Total ROI:</strong> 150%</p>
          <p><strong>Payout Schedule:</strong> Day 3, 6, 9, 12, 15 (5 cycles)</p>
        </div>

        <form onSubmit={handleActivation} className="activation-form">
          <div className="form-group">
            <label htmlFor="amount">Activation Amount (₱)</label>
            <input
              type="number"
              id="amount"
              value={activationAmount}
              onChange={handleAmountChange}
              placeholder="Enter amount (₱1,000 - ₱100,000)"
              min="1000"
              max="100000"
              step="100"
              required
              className={validationError ? 'input-error' : ''}
            />
          </div>

          {(error || validationError) && <div className="error-message">{error || validationError}</div>}
          {success && <div className="success-message">{success}</div>}

          <button 
            type="submit" 
            className="activation-btn"
            disabled={isActivating || walletBalance.credit < 1000 || validationError || !activationAmount}
          >
            {isActivating ? 'Activating...' : 'Activate Contract'}
          </button>
        </form>
      </div>

      {/* Active Contracts */}
      <div className="contracts-section">
        <h2>Your Active Contracts</h2>

        {investments.length === 0 ? (
          <div className="no-contracts">
            <p>No active contracts found. Activate your first contract above!</p>
          </div>
        ) : (
          <div className="contracts-grid">
            {investments.map((investment) => (
              <div key={investment._id} className="contract-card">
                <div className="contract-header">
                  <h3>Contract #{investment._id.slice(-6)}</h3>
                  <span className={`status ${investment.status}`}>
                    {investment.status.toUpperCase()}
                  </span>
                </div>

                <div className="contract-details">
                  <div className="detail-row">
                    <span>Capital:</span>
                    <span>₱{investment.amount.toLocaleString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Duration:</span>
                    <span>15 days</span>
                  </div>
                  <div className="detail-row">
                    <span>Payout:</span>
                    <span>30% every 3 days</span>
                  </div>
                  <div className="detail-row">
                    <span>Total ROI:</span>
                    <span>₱{(investment.amount * 1.5).toLocaleString()} (150%)</span>
                  </div>
                  <div className="detail-row">
                    <span>Start Date:</span>
                    <span>{formatDate(investment.startDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Maturity Date:</span>
                    <span>{formatDate(investment.maturityDate)}</span>
                  </div>
                  <div className="detail-row">
                    <span>Days Remaining:</span>
                    <span>{calculateDaysRemaining(investment.maturityDate)} days</span>
                  </div>
                  <div className="detail-row">
                    <span>Completed Payouts:</span>
                    <span>{investment.payoutSchedule?.filter(p => p.status === 'completed').length || 0} / 5</span>
                  </div>
                </div>

                {/* Payout Schedule */}
                <div className="payout-schedule">
                  <h4>Payout Schedule</h4>
                  <div className="schedule-grid">
                    {investment.payoutSchedule?.map((payout, index) => (
                      <div key={index} className={`payout-item ${payout.status}`}>
                        <span>Day {payout.day}</span>
                        <span>₱{payout.amount.toLocaleString()}</span>
                        <span className={`payout-status ${payout.status}`}>
                          {payout.status === 'completed' ? '✓' : payout.status === 'pending' ? '⏳' : '⏸'}
                        </span>
                      </div>
                    )) || (
                      <p>Payout schedule will be generated after activation</p>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Success Modal */}
      <SuccessModal
        isOpen={showSuccessModal}
        onClose={closeSuccessModal}
        title="Contract Activated Successfully!"
        message="Your investment contract has been activated and is now earning returns."
        details={successData}
      />
    </div>
  );
};

export default Investment;
