import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faWallet, 
  faChartBar, 
  faRocket, 
  faArrowTrendUp, 
  faPlusCircle, 
  faCog, 
  faExchangeAlt,
  faUsers,
  faCopy,
  faShare,
  faUpload,
  faDownload,
  faUser,
  faKey,
  faCreditCard,
  faClock,
  faGift
} from '@fortawesome/free-solid-svg-icons';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('summary');
  const [dashboardData, setDashboardData] = useState({
    totalInvestments: 0,
    activeInvestments: 0,
    totalReturns: 0,
    walletBalance: 0,
    passiveWalletBalance: 0,
    bonusWalletBalance: 0,
    creditWalletBalance: 0,
    encashmentRequests: 0,
    totalPayout: 0,
    directReferrals: 0,
    recentInvestments: [],
    depositRequests: [],
    withdrawalHistory: [],
    activePlans: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [depositForm, setDepositForm] = useState({ amount: '', receipt: null });
  const [depositMessage, setDepositMessage] = useState({ type: '', text: '' });
  const [withdrawalForm, setWithdrawalForm] = useState({ 
    walletType: 'passive', 
    payoutMethod: 'gcash', 
    amount: '', 
    payoutDetails: '' 
  });
  const [profileForm, setProfileForm] = useState({
    fullName: '',
    email: '',
    mobileNumber: '',
    username: ''
  });
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [activationAmount, setActivationAmount] = useState('');
  const [copySuccess, setCopySuccess] = useState(false);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      if (!token) {
        navigate('/login');
        return;
      }

      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };

      // Initialize wallets for existing users
      try {
        await axios.post('http://localhost:5000/api/v1/wallets/initialize', {}, config);
      } catch (walletError) {
        console.log('Wallet initialization:', walletError.response?.data?.message || 'Already initialized');
      }

      // Fetch unified dashboard data
      const dashboardRes = await axios.get('http://localhost:5000/api/v1/dashboard', config);
      const dashboardData = dashboardRes.data.data || {};

      // Fetch credit wallet specifically for consistency
      const creditWalletRes = await axios.get('http://localhost:5000/api/v1/wallet/credit', config);
      const creditWalletBalance = creditWalletRes.data.data.balance || 0;

      // Fetch additional data still needed
      const walletRes = await axios.get('http://localhost:5000/api/v1/wallets', config);
      const wallets = walletRes.data.data || [];

      const transactionsRes = await axios.get('http://localhost:5000/api/v1/transactions', config);
      const transactions = transactionsRes.data.data || [];

      const depositsRes = await axios.get('http://localhost:5000/api/v1/payments/my-deposits', config);
      const depositRequests = depositsRes.data.data || [];

      // Use contracts from dashboard data
      const investments = dashboardData.contracts || [];

      // Calculate dashboard metrics
      const totalInvestments = investments.length;
      const activeInvestments = investments.filter(inv => inv.status === 'active').length;
      const totalReturns = investments.reduce((sum, inv) => {
        return sum + (inv.status === 'completed' ? inv.totalReturn : 0);
      }, 0);

      // Get wallet balances
      const passiveWallet = wallets.find(wallet => wallet.walletType === 'passive');
      const bonusWallet = wallets.find(wallet => wallet.walletType === 'bonus');

      const walletBalance = passiveWallet ? passiveWallet.balance : 0;
      const passiveWalletBalance = passiveWallet ? passiveWallet.balance : 0;
      const bonusWalletBalance = bonusWallet ? bonusWallet.balance : 0;
      // creditWalletBalance is now fetched from the unified API above

      // Calculate encashment requests and total payout
      const encashmentRequests = transactions.filter(t => 
        t.transactionType === 'withdrawal' && t.status === 'pending'
      ).length;

      const totalPayout = transactions
        .filter(t => t.transactionType === 'withdrawal' && t.status === 'completed')
        .reduce((sum, t) => sum + t.amount, 0);

      // Get direct referrals count (mock for now)
      const directReferrals = 0; // Will be implemented with referral system

      // Get recent investments (last 5)
      const recentInvestments = investments
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        .slice(0, 5);

      // Get withdrawal history
      const withdrawalHistory = transactions.filter(t => t.transactionType === 'withdrawal');

      // Get active plans
      const activePlans = investments.filter(inv => inv.status === 'active');

      setDashboardData({
        totalInvestments,
        activeInvestments,
        totalReturns,
        walletBalance,
        passiveWalletBalance,
        bonusWalletBalance,
        creditWalletBalance,
        encashmentRequests,
        totalPayout,
        directReferrals,
        recentInvestments,
        depositRequests,
        withdrawalHistory,
        activePlans
      });

      // Set profile form data
      if (user) {
        setProfileForm({
          fullName: user.fullName || '',
          email: user.email || '',
          mobileNumber: user.mobileNumber || '',
          username: user.username || ''
        });
      }

      setLoading(false);
    } catch (error) {
      setError('Failed to fetch dashboard data');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  // Copy referral code to clipboard
  const copyReferralCode = async () => {
    try {
      await navigator.clipboard.writeText(user?.referralCode || '');
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    } catch (err) {
      console.error('Failed to copy referral code');
    }
  };

  // Handle deposit form submission
  const handleDepositSubmit = async (e) => {
    e.preventDefault();
    setDepositMessage({ type: '', text: '' }); // Clear previous messages

    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('amount', depositForm.amount);
      formData.append('receipt', depositForm.receipt);

      const response = await axios.post('http://localhost:5000/api/v1/payments/deposit', formData, {
        headers: { 
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      setDepositForm({ amount: '', receipt: null });
      setDepositMessage({ 
        type: 'success', 
        text: response.data.message || 'Deposit submitted successfully, awaiting admin approval'
      });
      fetchDashboardData(); // Refresh data
    } catch (error) {
      setDepositMessage({ 
        type: 'error', 
        text: error.response?.data?.message || 'Failed to submit deposit request'
      });
    }
  };

  // Handle wallet activation
  const handleActivation = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/v1/investments', {
        amount: parseFloat(activationAmount),
        package: '15-day-plan'
      });

      setActivationAmount('');
      fetchDashboardData(); // Refresh data
      alert('Wallet funds activated successfully!');
    } catch (error) {
      alert('Failed to activate wallet funds');
    }
  };

  // Handle withdrawal request
  const handleWithdrawalSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/v1/transactions/withdrawal', {
        walletType: withdrawalForm.walletType,
        payoutMethod: withdrawalForm.payoutMethod,
        amount: parseFloat(withdrawalForm.amount),
        payoutDetails: withdrawalForm.payoutDetails
      });

      setWithdrawalForm({ 
        walletType: 'passive', 
        payoutMethod: 'gcash', 
        amount: '', 
        payoutDetails: '' 
      });
      fetchDashboardData(); // Refresh data
      alert('Withdrawal request submitted successfully!');
    } catch (error) {
      alert('Failed to submit withdrawal request');
    }
  };

  // Handle profile update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.put('http://localhost:5000/api/v1/auth/updatedetails', profileForm);
      alert('Profile updated successfully!');
    } catch (error) {
      alert('Failed to update profile');
    }
  };

  // Handle password change
  const handlePasswordChange = async (e) => {
    e.preventDefault();
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    try {
      await axios.put('http://localhost:5000/api/v1/auth/updatepassword', {
        currentPassword: passwordForm.currentPassword,
        newPassword: passwordForm.newPassword
      });

      setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
      alert('Password changed successfully!');
    } catch (error) {
      alert('Failed to change password');
    }
  };

  // Get status badge class
  const getStatusBadge = (status) => {
    switch (status) {
      case 'completed': case 'approved': return 'status-success';
      case 'pending': return 'status-pending';
      case 'failed': case 'rejected': return 'status-error';
      default: return 'status-pending';
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome back, {user?.username}!</h1>
        <p>Manage your investments and account</p>
      </div>

      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-btn ${activeTab === 'summary' ? 'active' : ''}`}
          onClick={() => setActiveTab('summary')}
        >
          <FontAwesomeIcon icon={faChartBar} /> Summary
        </button>
        <button 
          className={`tab-btn ${activeTab === 'wallet' ? 'active' : ''}`}
          onClick={() => setActiveTab('wallet')}
        >
          <FontAwesomeIcon icon={faWallet} /> Wallet Funds
        </button>
        <button 
          className={`tab-btn ${activeTab === 'activation' ? 'active' : ''}`}
          onClick={() => setActiveTab('activation')}
        >
          <FontAwesomeIcon icon={faRocket} /> Activation
        </button>
        <button 
          className={`tab-btn ${activeTab === 'encashment' ? 'active' : ''}`}
          onClick={() => setActiveTab('encashment')}
        >
          <FontAwesomeIcon icon={faDownload} /> Encashment
        </button>
        <button 
          className={`tab-btn ${activeTab === 'settings' ? 'active' : ''}`}
          onClick={() => setActiveTab('settings')}
        >
          <FontAwesomeIcon icon={faUser} /> Settings
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {/* Summary Tab */}
        {activeTab === 'summary' && (
          <div className="summary-section">
            {/* Main Stats */}
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon wallet">
                  <FontAwesomeIcon icon={faWallet} />
                </div>
                <div className="stat-content">
                  <h3>{dashboardData.creditWalletBalance.toLocaleString()}</h3>
                  <p>Credit Wallet</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon passive">
                  <FontAwesomeIcon icon={faCreditCard} />
                </div>
                <div className="stat-content">
                  <h3>{dashboardData.passiveWalletBalance.toLocaleString()}</h3>
                  <p>Passive Wallet</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon bonus">
                  <FontAwesomeIcon icon={faGift} />
                </div>
                <div className="stat-content">
                  <h3>{dashboardData.bonusWalletBalance.toLocaleString()}</h3>
                  <p>Bonus Wallet</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon pending">
                  <FontAwesomeIcon icon={faClock} />
                </div>
                <div className="stat-content">
                  <h3>{dashboardData.encashmentRequests}</h3>
                  <p>Encashment Requests</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon returns">
                  <FontAwesomeIcon icon={faArrowTrendUp} />
                </div>
                <div className="stat-content">
                  <h3>{dashboardData.totalPayout.toLocaleString()}</h3>
                  <p>Total Payout</p>
                </div>
              </div>
            </div>

            {/* Referral Section */}
            <div className="referral-section">
              <div className="referral-card">
                <div className="referral-info">
                  <h3><FontAwesomeIcon icon={faUsers} /> Direct Referrals</h3>
                  <p className="referral-count">{dashboardData.directReferrals}</p>
                </div>
                <div className="referral-actions">
                  <div className="referral-code">
                    <label>Your Referral Code:</label>
                    <div className="code-input">
                      <input type="text" value={user?.referralCode || ''} readOnly />
                      <button onClick={copyReferralCode} className="copy-btn">
                        <FontAwesomeIcon icon={faCopy} />
                        {copySuccess ? 'Copied!' : 'Copy'}
                      </button>
                    </div>
                  </div>
                  <button className="refer-btn">
                    <FontAwesomeIcon icon={faShare} /> Refer a Friend
                  </button>
                </div>
              </div>
            </div>

            {/* Recent Investments */}
            <div className="recent-investments">
              <h2>Recent Investments</h2>
              {dashboardData.recentInvestments.length === 0 ? (
                <div className="no-data">
                  <p>No investments yet. Start your first investment!</p>
                </div>
              ) : (
                <div className="investments-list">
                  {dashboardData.recentInvestments.map((investment) => (
                    <div key={investment._id} className="investment-item">
                      <div className="investment-info">
                        <h4>Investment #{investment._id.slice(-6)}</h4>
                        <p>{investment.amount.toLocaleString()}  {investment.package.toUpperCase()}</p>
                      </div>
                      <div className="investment-status">
                        <span className={`status ${investment.status}`}>
                          {investment.status.toUpperCase()}
                        </span>
                        {investment.status === 'active' && (
                          <p className="days-remaining">
                            {calculateDaysRemaining(investment.endDate)} days left
                          </p>
                        )}
                      </div>
                      <div className="investment-date">
                        <p>{formatDate(investment.startDate)}</p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Wallet Funds Tab */}
        {activeTab === 'wallet' && (
          <div className="wallet-section">
            <div className="section-header">
              <h2><FontAwesomeIcon icon={faUpload} /> Deposit Funds</h2>
              <p>Upload your payment receipt for verification</p>
            </div>

            <form onSubmit={handleDepositSubmit} className="deposit-form">
              {depositMessage.text && (
                <div className={`message ${depositMessage.type}`}>
                  {depositMessage.text}
                </div>
              )}

              <div className="form-group">
                <label>Amount ()</label>
                <input
                  type="number"
                  value={depositForm.amount}
                  onChange={(e) => setDepositForm({...depositForm, amount: e.target.value})}
                  placeholder="Enter amount"
                  required
                />
              </div>

              <div className="form-group">
                <label>Payment Receipt</label>
                <input
                  type="file"
                  accept="image/*,application/pdf"
                  onChange={(e) => setDepositForm({...depositForm, receipt: e.target.files[0]})}
                  required
                />
              </div>

              <button type="submit" className="submit-btn">
                <FontAwesomeIcon icon={faUpload} /> Submit Deposit Request
              </button>
            </form>

            {/* Wallet Balance Display */}
            <div className="wallet-balance-display">
              <h3>Credit Wallet Balance</h3>
              <div className="balance-card">
                <div className="balance-amount">
                  {dashboardData.creditWalletBalance.toLocaleString()}
                </div>
                <p>Credit Wallet Balance</p>
              </div>
            </div>

            {/* Deposit History */}
            <div className="deposit-history">
              <h3>Deposit Transaction History</h3>
              {dashboardData.depositRequests.length === 0 ? (
                <div className="no-data">
                  <p>No deposit requests yet.</p>
                </div>
              ) : (
                <div className="table-container">
                  <table className="deposit-table">
                    <thead>
                      <tr>
                        <th>Amount</th>
                        <th>Date</th>
                        <th>Status</th>
                        <th>Receipt</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dashboardData.depositRequests.map((deposit) => (
                        <tr key={deposit._id}>
                          <td>{deposit.amount.toLocaleString()}</td>
                          <td>{formatDate(deposit.createdAt)}</td>
                          <td>
                            <span className={`status-badge ${getStatusBadge(deposit.status)}`}>
                              {deposit.status.charAt(0).toUpperCase() + deposit.status.slice(1)}
                            </span>
                          </td>
                          <td>
                            {deposit.receipt ? (
                              <a 
                                href={`http://localhost:5000${deposit.receipt}`} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="view-receipt-btn"
                              >
                                View Receipt
                              </a>
                            ) : (
                              <span className="no-receipt">No receipt</span>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Activation Tab */}
        {activeTab === 'activation' && (
          <div className="activation-section">
            <div className="section-header">
              <h2><FontAwesomeIcon icon={faRocket} /> Activate Wallet Funds</h2>
              <p>Activate your wallet funds for a 15-day investment plan</p>
            </div>

            <form onSubmit={handleActivation} className="activation-form">
              <div className="form-group">
                <label>Activation Amount ()</label>
                <input
                  type="number"
                  value={activationAmount}
                  onChange={(e) => setActivationAmount(e.target.value)}
                  placeholder="Enter amount to activate"
                  max={dashboardData.creditWalletBalance}
                  required
                />
                <small>Available Balance: {dashboardData.creditWalletBalance.toLocaleString()}</small>
              </div>

              <div className="plan-details">
                <h4>15-Day Plan Details:</h4>
                <ul>
                  <li>Duration: 15 days</li>
                  <li>Payout: 30% every 3 days</li>
                  <li>Total ROI: 150%</li>
                  <li>Payout Schedule: Day 3, 6, 9, 12, 15 (5 cycles)</li>
                </ul>
              </div>

              <button type="submit" className="submit-btn">
                <FontAwesomeIcon icon={faRocket} /> Activate Now
              </button>
            </form>

            {/* Active Plans */}
            <div className="active-plans">
              <h3>Active Plans</h3>
              {dashboardData.activePlans.length === 0 ? (
                <div className="no-data">
                  <p>No active plans yet.</p>
                </div>
              ) : (
                <div className="plans-list">
                  {dashboardData.activePlans.map((plan) => (
                    <div key={plan._id} className="plan-item">
                      <div className="plan-info">
                        <h4>{plan.amount.toLocaleString()}</h4>
                        <p>Start: {formatDate(plan.startDate)}</p>
                        <p>Maturity: {formatDate(plan.endDate)}</p>
                      </div>
                      <div className="plan-progress">
                        <p>{calculateDaysRemaining(plan.endDate)} days remaining</p>
                        <div className="progress-bar">
                          <div 
                            className="progress-fill" 
                            style={{width: `${((15 - calculateDaysRemaining(plan.endDate)) / 15) * 100}%`}}
                          ></div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Encashment Tab */}
        {activeTab === 'encashment' && (
          <div className="encashment-section">
            <div className="section-header">
              <h2><FontAwesomeIcon icon={faDownload} /> Request Withdrawal</h2>
              <p>Withdraw funds from your wallets</p>
            </div>

            <form onSubmit={handleWithdrawalSubmit} className="withdrawal-form">
              <div className="form-row">
                <div className="form-group">
                  <label>Source Wallet</label>
                  <select
                    value={withdrawalForm.walletType}
                    onChange={(e) => setWithdrawalForm({...withdrawalForm, walletType: e.target.value})}
                  >
                    <option value="passive">Passive Wallet ({dashboardData.passiveWalletBalance.toLocaleString()})</option>
                    <option value="bonus">Bonus Wallet ({dashboardData.bonusWalletBalance.toLocaleString()})</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Payout Method</label>
                  <select
                    value={withdrawalForm.payoutMethod}
                    onChange={(e) => setWithdrawalForm({...withdrawalForm, payoutMethod: e.target.value})}
                  >
                    <option value="gcash">GCash</option>
                    <option value="maya">Maya</option>
                    <option value="gotyme">GoTyme</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Amount ()</label>
                <input
                  type="number"
                  value={withdrawalForm.amount}
                  onChange={(e) => setWithdrawalForm({...withdrawalForm, amount: e.target.value})}
                  placeholder="Enter withdrawal amount"
                  required
                />
              </div>

              <div className="form-group">
                <label>Payout Details</label>
                <input
                  type="text"
                  value={withdrawalForm.payoutDetails}
                  onChange={(e) => setWithdrawalForm({...withdrawalForm, payoutDetails: e.target.value})}
                  placeholder="Enter account number/details"
                  required
                />
              </div>

              <button type="submit" className="submit-btn">
                <FontAwesomeIcon icon={faDownload} /> Request Withdrawal
              </button>
            </form>

            {/* Withdrawal History */}
            <div className="withdrawal-history">
              <h3>Withdrawal History</h3>
              {dashboardData.withdrawalHistory.length === 0 ? (
                <div className="no-data">
                  <p>No withdrawal requests yet.</p>
                </div>
              ) : (
                <div className="history-list">
                  {dashboardData.withdrawalHistory.map((withdrawal) => (
                    <div key={withdrawal._id} className="history-item">
                      <div className="item-info">
                        <h4>{withdrawal.amount.toLocaleString()}</h4>
                        <p>{withdrawal.payoutMethod?.toUpperCase()}</p>
                        <p>{formatDate(withdrawal.createdAt)}</p>
                      </div>
                      <div className="item-status">
                        <span className={`status-badge ${getStatusBadge(withdrawal.status)}`}>
                          {withdrawal.status.toUpperCase()}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Settings Tab */}
        {activeTab === 'settings' && (
          <div className="settings-section">
            <div className="settings-grid">
              {/* Profile Settings */}
              <div className="settings-card">
                <div className="card-header">
                  <h3><FontAwesomeIcon icon={faUser} /> Profile Information</h3>
                </div>
                <form onSubmit={handleProfileUpdate} className="profile-form">
                  <div className="form-group">
                    <label>Full Name</label>
                    <input
                      type="text"
                      value={profileForm.fullName}
                      onChange={(e) => setProfileForm({...profileForm, fullName: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      value={profileForm.email}
                      onChange={(e) => setProfileForm({...profileForm, email: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Mobile Number</label>
                    <input
                      type="text"
                      value={profileForm.mobileNumber}
                      onChange={(e) => setProfileForm({...profileForm, mobileNumber: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Username</label>
                    <input
                      type="text"
                      value={profileForm.username}
                      onChange={(e) => setProfileForm({...profileForm, username: e.target.value})}
                      required
                    />
                  </div>

                  <button type="submit" className="submit-btn">
                    <FontAwesomeIcon icon={faUser} /> Update Profile
                  </button>
                </form>
              </div>

              {/* Password Settings */}
              <div className="settings-card">
                <div className="card-header">
                  <h3><FontAwesomeIcon icon={faKey} /> Change Password</h3>
                </div>
                <form onSubmit={handlePasswordChange} className="password-form">
                  <div className="form-group">
                    <label>Current Password</label>
                    <input
                      type="password"
                      value={passwordForm.currentPassword}
                      onChange={(e) => setPasswordForm({...passwordForm, currentPassword: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>New Password</label>
                    <input
                      type="password"
                      value={passwordForm.newPassword}
                      onChange={(e) => setPasswordForm({...passwordForm, newPassword: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Confirm New Password</label>
                    <input
                      type="password"
                      value={passwordForm.confirmPassword}
                      onChange={(e) => setPasswordForm({...passwordForm, confirmPassword: e.target.value})}
                      required
                    />
                  </div>

                  <button type="submit" className="submit-btn">
                    <FontAwesomeIcon icon={faKey} /> Change Password
                  </button>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
