import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import SuccessModal from '../components/SuccessModal';
import { Card, Table, InfoBox } from '../components/UI';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [creditWallet, setCreditWallet] = useState(0);
  const [amount, setAmount] = useState('');
  const [contracts, setContracts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isActivating, setIsActivating] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [successData, setSuccessData] = useState(null);
  const [validationError, setValidationError] = useState('');

  useEffect(() => {
    fetchCreditWalletBalance();
    fetchActiveContracts();
  }, []);

  const fetchCreditWalletBalance = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/wallet/credit', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.status === 'success') {
        setCreditWallet(res.data.balance || 0);
      }
    } catch (error) {
      console.error('Failed to fetch credit wallet balance:', error);
      setCreditWallet(0);
    }
  };

  const fetchActiveContracts = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/investments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setContracts(res.data.data || []);
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch contracts:', error);
      setContracts([]);
      setLoading(false);
    }
  };

  const validateAmount = (value) => {
    const amountNum = parseFloat(value);

    if (!value || value === '') {
      setValidationError('');
      return;
    }

    if (isNaN(amountNum) || amountNum <= 0) {
      setValidationError('Please enter a valid amount');
      return;
    }

    if (amountNum < 1000) {
      setValidationError('Minimum activation is ₱1,000');
      return;
    }

    if (amountNum > 100000) {
      setValidationError('Maximum activation is ₱100,000');
      return;
    }

    if (amountNum > creditWallet) {
      setValidationError('Insufficient Credit Wallet balance');
      return;
    }

    setValidationError('');
  };

  const handleAmountChange = (e) => {
    const value = e.target.value;
    setAmount(value);
    validateAmount(value);
  };

  const handleActivate = async () => {
    if (validationError || !amount) {
      setError(validationError || 'Please enter an amount');
      return;
    }

    setIsActivating(true);
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('token');
      const amountNum = parseFloat(amount);

      const res = await axios.post('/api/v1/investments/activate', 
        { amount: amountNum }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.data.status === 'success') {
        setSuccessData({
          amount: amountNum,
          updatedBalance: res.data.data.updatedBalance,
          investment: res.data.data.investment
        });
        setShowSuccessModal(true);
        setAmount('');
        setValidationError('');
        fetchCreditWalletBalance();
        fetchActiveContracts();
      } else {
        setError(res.data.message || 'Activation failed');
      }
    } catch (err) {
      console.error('Activation error:', err);
      if (err.response?.data?.status === 'failed') {
        setError(err.response.data.message);
      } else {
        setError('Failed to activate wallet funds: ' + (err.message || 'Unknown error'));
      }
    } finally {
      setIsActivating(false);
    }
  };

  const closeSuccessModal = () => {
    setShowSuccessModal(false);
    setSuccessData(null);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const calculateDaysRemaining = (endDate) => {
    const today = new Date();
    const end = new Date(endDate);
    const diffTime = end - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Dashboard</h1>
        <p>Manage your investments and track your portfolio</p>
      </div>

      {/* Credit Wallet Balance */}
      <Card variant="wallet" className="wallet-balance-card">
        <div className="card-header">
          <h2>Credit Wallet Balance</h2>
        </div>
        <div className="balance-display">
          <span className="currency">₱</span>
          <span className="amount">{creditWallet.toLocaleString()}</span>
        </div>
        <p className="balance-subtitle">Available for investment activation</p>
      </Card>

      {/* Investment Activation */}
      <Card variant="investment" className="activation-card">
        <div className="card-header">
          <h3>Activate New Contract</h3>
          <p>Start earning with our 15-day investment plan</p>
        </div>

        <InfoBox 
          icon="fas fa-chart-line" 
          title="Investment Plan Details"
          variant="info"
          className="plan-details"
        >
          <p><strong>Duration:</strong> 15 days</p>
          <p><strong>Payout:</strong> 30% every 3 days</p>
          <p><strong>Total ROI:</strong> 150%</p>
          <p><strong>Payout Schedule:</strong> Day 3, 6, 9, 12, 15 (5 cycles)</p>
          <p><strong>Min/Max:</strong> ₱1,000 - ₱100,000</p>
        </InfoBox>

        <div className="activation-form">
          <input
            type="number"
            placeholder="Enter amount (₱1,000 - ₱100,000)"
            value={amount}
            onChange={handleAmountChange}
            min="1000"
            max="100000"
          />
          {validationError && (
            <InfoBox variant="error" className="compact">
              {validationError}
            </InfoBox>
          )}
          {error && (
            <InfoBox variant="error" className="compact">
              {error}
            </InfoBox>
          )}
          {success && (
            <InfoBox variant="success" className="compact">
              {success}
            </InfoBox>
          )}
          <button 
            onClick={handleActivate}
            disabled={isActivating || validationError || !amount}
            className={`activate-btn ${isActivating ? 'activating' : ''}`}
          >
            {isActivating ? (
              <>
                <i className="fas fa-spinner fa-spin"></i>
                Activating...
              </>
            ) : (
              <>
                <i className="fas fa-rocket"></i>
                Activate Contract
              </>
            )}
          </button>
        </div>
      </Card>

      {/* Active Contracts */}
      <Card variant="earnings" className="contracts-card">
        <div className="card-header">
          <h3>Your Active Contracts</h3>
          <p>Track your investment portfolio and earnings</p>
        </div>

        {contracts.length === 0 ? (
          <div className="no-contracts">
            <div className="no-contracts-icon">
              <i className="fas fa-chart-pie"></i>
            </div>
            <h4>No Active Contracts</h4>
            <p>Start your investment journey by activating your first contract above.</p>
          </div>
        ) : (
          <Table striped hover>
            <Table.Header>
              <Table.Row>
                <Table.Cell header>Amount</Table.Cell>
                <Table.Cell header>Duration</Table.Cell>
                <Table.Cell header>Start Date</Table.Cell>
                <Table.Cell header>Maturity Date</Table.Cell>
                <Table.Cell header>Days Left</Table.Cell>
                <Table.Cell header>Status</Table.Cell>
                <Table.Cell header>Expected Return</Table.Cell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {contracts.map((contract, index) => (
                <Table.Row key={index}>
                  <Table.Cell>
                    <span className="amount positive">₱{contract.amount?.toLocaleString()}</span>
                  </Table.Cell>
                  <Table.Cell>{contract.duration || 15} days</Table.Cell>
                  <Table.Cell>
                    <span className="date">{formatDate(contract.startDate)}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className="date">{formatDate(contract.maturityDate)}</span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className={calculateDaysRemaining(contract.maturityDate) <= 3 ? 'amount negative' : 'amount'}>
                      {calculateDaysRemaining(contract.maturityDate)} days
                    </span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className={`status-badge ${contract.status?.toLowerCase()}`}>
                      {contract.status}
                    </span>
                  </Table.Cell>
                  <Table.Cell>
                    <span className="amount positive">₱{(contract.amount * 1.5)?.toLocaleString()}</span>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
        )}
      </Card>



      {/* Success Modal */}
      {showSuccessModal && successData && (
        <SuccessModal
          isOpen={showSuccessModal}
          onClose={closeSuccessModal}
          title="Contract Activated Successfully!"
          message={`Your contract of ₱${successData.amount?.toLocaleString()} has been activated successfully.`}
          details={[
            `Investment Amount: ₱${successData.amount?.toLocaleString()}`,
            `Updated Credit Balance: ₱${successData.updatedBalance?.toLocaleString()}`,
            `Contract Duration: 15 days`,
            `Expected Total Return: ₱${(successData.amount * 1.5)?.toLocaleString()}`
          ]}
        />
      )}
    </div>
  );
};

export default Dashboard;
