import React, { useState } from 'react';
import './ReferralLink.css';

const ReferralLink = ({ link }) => {
  const [copied, setCopied] = useState(false);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(link);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="referral-link-centered">
      <h3 className="referral-title-centered">Your Referral Link</h3>
      <div className="referral-link-box-clean">
        <span className="referral-url-clean">{link}</span>
        <button 
          onClick={copyToClipboard}
          className={`copy-button-clean ${copied ? 'copied' : ''}`}
        >
          {copied ? "Copied!" : "Copy"}
        </button>
      </div>
    </div>
  );
};

export default ReferralLink;
