import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Transactions.css';

const Transactions = () => {
  const { user } = useAuth();
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all'); // all, deposit, withdrawal, investment, activation, earnings, referral

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/v1/transactions');
      setTransactions(res.data.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch transactions');
      setLoading(false);
    }
  };

  const getTransactionIcon = (type) => {
    switch (type?.toLowerCase()) {
      case 'deposit':
        return 'ðŸ’°';
      case 'withdrawal':
        return 'ðŸ’¸';
      case 'investment':
      case 'activation':
        return 'ðŸ“ˆ';
      case 'return':
      case 'earnings':
      case 'payout':
        return 'ðŸ’Ž';
      case 'transfer':
      case 'referral':
      case 'commission':
        return 'ðŸ”„';
      default:
        return 'ðŸ“„';
    }
  };

  const getTransactionColor = (type) => {
    switch (type?.toLowerCase()) {
      case 'deposit':
      case 'return':
      case 'earnings':
      case 'payout':
      case 'referral':
      case 'commission':
        return 'positive';
      case 'withdrawal':
      case 'investment':
      case 'activation':
        return 'negative';
      case 'transfer':
        return 'neutral';
      default:
        return 'neutral';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'success';
      case 'pending':
        return 'warning';
      case 'failed':
      case 'cancelled':
        return 'danger';
      default:
        return 'secondary';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatAmount = (amount, type) => {
    const positiveTypes = ['deposit', 'return', 'earnings', 'payout', 'referral', 'commission'];
    const sign = positiveTypes.includes(type?.toLowerCase()) ? '+' : '-';
    return `${sign}â‚±${Math.abs(amount).toLocaleString()}`;
  };

  const filteredTransactions = transactions.filter(transaction => {
    if (filter === 'all') return true;
    return transaction.type === filter;
  });

  if (loading) {
    return <div className="loading">Loading transactions...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="transactions-container">
      <div className="transactions-header">
        <h1>Transaction History</h1>
        <p>View all your financial transactions and activities</p>
      </div>

      {/* Filter Tabs */}
      <div className="filter-tabs">
        <button 
          className={`filter-tab ${filter === 'all' ? 'active' : ''}`}
          onClick={() => setFilter('all')}
        >
          All Transactions
        </button>
        <button 
          className={`filter-tab ${filter === 'deposit' ? 'active' : ''}`}
          onClick={() => setFilter('deposit')}
        >
          Deposits
        </button>
        <button 
          className={`filter-tab ${filter === 'activation' ? 'active' : ''}`}
          onClick={() => setFilter('activation')}
        >
          Activations
        </button>
        <button 
          className={`filter-tab ${filter === 'earnings' ? 'active' : ''}`}
          onClick={() => setFilter('earnings')}
        >
          Earnings
        </button>
        <button 
          className={`filter-tab ${filter === 'referral' ? 'active' : ''}`}
          onClick={() => setFilter('referral')}
        >
          Referrals
        </button>
        <button 
          className={`filter-tab ${filter === 'withdrawal' ? 'active' : ''}`}
          onClick={() => setFilter('withdrawal')}
        >
          Withdrawals
        </button>
      </div>

      {/* Transactions List */}
      <div className="transactions-section">
        {filteredTransactions.length === 0 ? (
          <div className="no-transactions">
            <div className="no-transactions-icon">ðŸ“„</div>
            <h3>No transactions found</h3>
            <p>
              {filter === 'all' 
                ? "You haven't made any transactions yet. Start by making your first investment!"
                : `No ${filter} transactions found. Try selecting a different filter.`
              }
            </p>
          </div>
        ) : (
          <div className="transactions-list">
            {filteredTransactions.map((transaction) => (
              <div key={transaction._id} className="transaction-item">
                <div className="transaction-icon">
                  {getTransactionIcon(transaction.type)}
                </div>

                <div className="transaction-details">
                  <div className="transaction-main">
                    <h4>{transaction?.type ? transaction.type.charAt(0).toUpperCase() + transaction.type.slice(1) : "Unknown"}</h4>
                    <p className="transaction-description">
                      {transaction.description || `${transaction?.type || "Unknown"} transaction`}
                    </p>
                  </div>

                  <div className="transaction-meta">
                    <span className="transaction-date">
                      {formatDate(transaction.createdAt)}
                    </span>
                    <span className="transaction-wallet">
                      {transaction.walletType} wallet
                    </span>
                  </div>
                </div>

                <div className="transaction-amount">
                  <span className={`amount ${getTransactionColor(transaction.type)}`}>
                    {formatAmount(transaction.amount, transaction.type)}
                  </span>
                  <span className={`status ${getStatusColor(transaction.status)}`}>
                    {transaction.status.toUpperCase()}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Transaction Summary */}
      {transactions.length > 0 && (
        <div className="transaction-summary">
          <h3>Summary</h3>
          <div className="summary-grid">
            <div className="summary-item">
              <span className="summary-label">Total Transactions:</span>
              <span className="summary-value">{transactions.length}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Total Deposits:</span>
              <span className="summary-value positive">
                +â‚±{transactions
                  .filter(t => t.type === 'deposit' && t.status === 'completed')
                  .reduce((sum, t) => sum + t.amount, 0)
                  .toLocaleString()}
              </span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Total Withdrawals:</span>
              <span className="summary-value negative">
                -â‚±{transactions
                  .filter(t => t.type === 'withdrawal' && t.status === 'completed')
                  .reduce((sum, t) => sum + t.amount, 0)
                  .toLocaleString()}
              </span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Total Investments:</span>
              <span className="summary-value neutral">
                â‚±{transactions
                  .filter(t => t.type === 'investment' && t.status === 'completed')
                  .reduce((sum, t) => sum + t.amount, 0)
                  .toLocaleString()}
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Transactions;
