import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { Card, InfoBox } from '../components/UI';
import './Settings.css';

const Settings = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState({
    firstName: '',
    lastName: '',
    email: '',
    mobile: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/v1/auth/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.status === 'success') {
        setProfile({
          firstName: res.data.data.firstName || '',
          lastName: res.data.data.lastName || '',
          email: res.data.data.email || '',
          mobile: res.data.data.mobile || ''
        });
      }
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch profile:', error);
      setError('Failed to load profile data');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    setIsUpdating(true);
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('token');
      const res = await axios.put('/api/v1/auth/profile', profile, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.status === 'success') {
        setSuccess('Profile updated successfully!');
      } else {
        setError(res.data.message || 'Failed to update profile');
      }
    } catch (err) {
      console.error('Profile update error:', err);
      setError(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setIsUpdating(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading settings...</div>;
  }

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>Account Settings</h1>
        <p>Manage your account information and preferences</p>
      </div>

      <div className="settings-content">
        <Card variant="settings" className="profile-card">
          <h2>Profile Information</h2>

          <form onSubmit={handleUpdateProfile} className="profile-form">
            <div className="form-group">
              <label htmlFor="firstName">First Name</label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={profile.firstName}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="lastName">Last Name</label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={profile.lastName}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                value={profile.email}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="mobile">Mobile Number</label>
              <input
                type="tel"
                id="mobile"
                name="mobile"
                value={profile.mobile}
                onChange={handleInputChange}
                required
              />
            </div>

            {error && (
              <InfoBox variant="error" className="message-box">
                {error}
              </InfoBox>
            )}
            {success && (
              <InfoBox variant="success" className="message-box">
                {success}
              </InfoBox>
            )}

            <button 
              type="submit" 
              className="update-btn"
              disabled={isUpdating}
            >
              {isUpdating ? 'Updating...' : 'Update Profile'}
            </button>
          </form>
        </Card>

        <Card variant="settings" className="account-info-card">
          <h2>Account Information</h2>
          <div className="info-list">
            <div className="info-item">
              <span className="label">User ID:</span>
              <span className="value">{user?.id || 'N/A'}</span>
            </div>
            <div className="info-item">
              <span className="label">Account Type:</span>
              <span className="value">{user?.role || 'User'}</span>
            </div>
            <div className="info-item">
              <span className="label">Member Since:</span>
              <span className="value">
                {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
              </span>
            </div>
          </div>

          <div style={{marginTop: '2rem', paddingTop: '1.5rem', borderTop: '1px solid rgba(148, 163, 184, 0.1)'}}>
            <h3 style={{color: '#00ff88', marginBottom: '1rem', fontSize: '1.2rem'}}>Referral</h3>
            <p style={{color: '#94a3b8', marginBottom: '1rem', fontSize: '0.95rem'}}>Invite new users to join the platform</p>
            <a 
              href="/register" 
              className="update-btn" 
              style={{display: 'inline-block', textDecoration: 'none', textAlign: 'center', width: '100%'}}
            >
              Register New User
            </a>
          </div>
        </Card>
      </div>
    </div>
  );
};

export default Settings;
